con:config.script:
allPlayers = testRunner.testCase.testSuite.project.getPropertyValue("playerIds")

groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
slurper = new groovy.json.JsonSlurper()
result = slurper.parseText( allPlayers )

context.loopIndex = 0	
while(context.loopIndex < result.size)
{
	String playerId = result[context.loopIndex].id
	groovyUtils.setPropertyValue("Delete Player", "playerId", playerId)
	if(++context.loopIndex < result.size)
	{
		testRunner.runTestStepByName("Delete Player")
	}
}

